import React from "react";
import closeIcon from "./close-icon.png";
import "./Notifications.css";

function Notifications() {
  const handleButtonClick = () => {
    console.log("Close button has been clicked");
  };

  return (
    <div className="Notifications">
      <button
        style={{
          float: "right", // Show button on the right side
        }}
        aria-label="Close"
        onClick={handleButtonClick}
      >
        <img src={closeIcon} alt="Close" />
      </button>
      <p>Here is the list of notifications</p>
      <ul>
        <li data-priority="default">New course available</li>
        <li data-priority="urgent">New resume available</li>
        <li
          dangerouslySetInnerHTML={{
            __html: getLatestNotification(), // Use dangerouslySetInnerHTML to render HTML content
          }}
        ></li>
      </ul>
    </div>
  );
}

// Mock function for getLatestNotification
function getLatestNotification() {
  return "<strong>Important:</strong> You have a new message.";
}

export default Notifications;
